mapper.insert:
	
     SELECT CONCAT(#{prefix} , LPAD(CAST(REPLACE(IFNULL(MAX(CATE_ID),0), #{prefix},'') AS UNSIGNED) + 1, 6, '0')) AS cateId FROM CATE_INFO
    		
    

	INSERT INTO CATE_INFO(
	 CATE_ID
	, CATE_NM
	, UP_CATE_ID
	, DEL_YN
	, CATE_ORD_NO
	, REG_ID
	, REG_DTTM
	, UPD_ID
	, UPD_DTTM
	) VALUES (
	 #{cateId}
	, #{cateNm}
	, #{upCateId}
	, #{delYn}
	, #{cateOrdNo}
	, #{regId}
	, NOW()
	, #{updId}
	, NOW()
	)
