mapper.select:
SELECT A.NOTI_SEQ , A.NOTI_ID , A.BOARD_ID , A.CATE_ID , A.UP_NOTI_ID , A.NOTI_KIND , A.NOTI_TITLE , A.NOTI_CONTS , A.NOTI_URL , A.NOTI_OPN_DIV , A.FB_REG_YN , A.DEL_YN , A.STAT_CD , A.USER_NM , A.NOTI_READ_CNT , A.NOTI_OK_CNT , A.NOTI_NG_CNT , A.WRITE_IP , A.NOTI_BGN_DTTM , A.NOTI_END_DTTM , A.SCRAP_CNT , A.NOTI_OPN_CNT , A.REG_ID , CAST( DATE_FORMAT( A.REG_DTTM,'%Y-%m-%d %T' ) AS CHAR(19) ) REG_DTTM , A.UPD_ID , A.UPD_DTTM , A.REF , A.RESTEP , A.RELEVEL , A.ALARM_YN , A.PRVT_YN FROM NOTI_INFO A WHERE A.DEL_YN = 'N' AND A.BOARD_ID = #{boardId}
AND A.NOTI_TITLE LIKE CONCAT('%', #{searchValue} , '%')
AND A.NOTI_CONTS LIKE CONCAT('%', #{searchValue} , '%')
AND A.USER_NM LIKE CONCAT('%', #{searchValue} , '%')
ORDER BY A.ALARM_YN DESC, A.REF DESC, A.RESTEP ASC LIMIT #{fromLimit}, #{toLimit}

    
	SELECT IFNULL(MAX(NOTI_SEQ),0)+1 NOTI_SEQ
	FROM NOTI_INFO
	WHERE BOARD_ID = #{boardId}

mapper.insert:
	
	 SELECT CONCAT('C' , LPAD(CAST(REPLACE(IFNULL(MAX(NOTI_ID),0), 'C','') AS UNSIGNED) + 1, 8, '0')) AS notiId FROM NOTI_INFO
     WHERE NOTI_ID LIKE 'C%'	    
    
  
	
	INSERT INTO NOTI_INFO(
