	, #{userId}
	, #{userNm}
	, #{notiReadCnt}
	, #{notiOkCnt}
	, #{notiNgCnt}
	, #{writeIp}
	, NOW()
	, CAST( '9999-12-31 23:59:59' AS DATETIME )
	, #{scrapCnt}
