mapper.select:
SELECT USER_ID ,USER_NM ,EMAIL ,PWD ,MOBILE ,NATION_NM ,BIRTH_YMD ,SEX ,USER_URL ,FB_ID ,MAIL_RECV_YN ,END_DTTM ,END_YN ,USER_FILE_NM ,USER_FILE_PATH ,REG_ID ,REG_DTTM ,UPD_ID ,UPD_DTTM FROM PSN_USER WHERE 1 = 1
AND USER_ID = #{userId}
AND PWD = #{pwd}
AND EMAIL = #{email}
LIMIT 1

mapper.insert:

	INSERT INTO PSN_USER(
