mapper.update:

    UPDATE PSN_USER SET UPD_ID = #{updId}, UPD_DTTM = NOW()
    
     , MOBILE = #{mobile} 
     , BIRTH_YMD = #{birthYmd} 
     , SEX = #{sex} 
     , NATION_NM = #{nationNm} 
     , USER_URL = #{userUrl} 
     , FB_ID = #{fbId} 
     , MAIL_RECV_YN = #{mailRecvYn} 
     , MAIL_RECV_YN = #{mailRecvYn} 
