mapper.insert:
	
	  SELECT IFNULL(MAX(PSN_OPN_SEQ),0)+1 AS psnOpnSeq FROM PSN_OPN	     	    
    
    	
    
		INSERT INTO PSN_OPN(
		  PSN_OPN_SEQ
		, USER_ID
		, UP_PSN_OPN_SEQ
		, OPN_CONTS
		, WRITE_IP
		, REF
		, RELEVEL
		, RESTEP
		, DEL_YN
		, REG_ID
		, REG_DTTM
		, UPD_ID
		, UPD_DTTM
		) VALUES (
		 #{psnOpnSeq}
		, #{userId}
		, #{upPsnOpnSeq}
		, #{opnConts}
		, #{writeIp}
		, #{psnOpnSeq} /* no ref */
		, #{relevel}
		, #{restep}
		, 'N'
		, #{regId}
		, NOW()
		, #{updId}
		, NOW()
		)

mapper.update:
    
		UPDATE PSN_OPN
		SET RESTEP = RESTEP + 1
		WHERE USER_ID = #{userId}
		AND   PSN_OPN_SEQ = #{psnOpnSeq}
		AND   DEL_YN = 'N'
		AND   REF = #{ref}
		AND   RESTEP > #{restep}

