mapper.delete:
DELETE FROM USER_SCRAP_INFO
WHERE USER_ID = #{userId}
AND NOTI_ID = #{notiId}

UPDATE NOTI_INFO
SET STAT_CD = NULL, DEL_YN = 'N'
WHERE NOTI_ID = #{notiId}
AND REG_ID = #{userId}
AND STAT_CD = '9'

DELETE FROM NOTI_INFO
WHERE NOTI_ID = #{notiId}
AND REG_ID = #{userId}
AND STAT_CD = '9'
